targets.expr:
count(
  count(
    ceph_osd_up + on (ceph_daemon) group_left(instance) ceph_disk_occupation
  ) by (instance) > 0
)

count(
  count(
    ceph_osd_up + on (ceph_daemon) group_left(instance) ceph_disk_occupation
  ) by (instance) == 0
)

quantile(
  $percentile / 100, (
    avg (
      irate(
          node_cpu{mode=~"system|user|iowait", instance=~'[[osd_servers]]'}[1m]
        )
    ) by (instance)
  ) * 100
)

sum(
  sum(
    rate(
      node_disk_reads_completed{instance=~"[[osd_servers]]"}[$__interval]
    )
  ) by (instance,device)
)
+
sum(
  sum(
    rate(
      node_disk_writes_completed{instance=~"[[osd_servers]]"}[$__interval]
    )
  ) by (instance,device)
)

# should only include OSD hosts
sum(
  sum(
    rate(
      node_disk_bytes_read[$__interval]
    )
  ) by (instance,device)
  + ignoring(ceph_daemon,job) group_right(instance) ceph_disk_occupation
)
+
sum(
  sum(
    rate(
      node_disk_bytes_written[$__interval]
    )
  ) by (instance,device)
  + ignoring(ceph_daemon,job) group_right(instance) ceph_disk_occupation
)

# will include non-OSD hosts
sum(
  rate(
    node_disk_bytes_read[$__interval]
  ) + 
  rate(
    node_disk_bytes_written[$__interval]
  )
)

quantile(
  $percentile/100, 
  (
    node_memory_MemTotal{job='node'} - node_memory_MemFree{job='node'} - node_memory_Buffers{job='node'} - node_memory_Cached{job='node'}
  ) / node_memory_MemTotal{job='node'} * 100
)

avg (
  max by (device) (
    irate(node_disk_io_time_ms[5m]) 
    and on (instance, device) ceph_disk_occupation{instance=~"($osd_servers)"}
  ) / 10
)

quantile(
  $percentile/100, 
  (
    max by (device) (
      irate(node_disk_io_time_ms[5m]) 
      and on (instance, device) ceph_disk_occupation{instance=~"($osd_servers)"}
    ) / 10
  )
)
